import React, { Component } from "react";
import NavBar from "./components/navbar";
import "./App.css";

class App extends Component {
  state = {
    title: null,
    content: null,
    counter: 0
  };

  constructor(props) {
    super(props);
    console.log("App - Constructor");
  }

  connecToServer() {  fetch('/');  }

  componentDidMount() {
    fetch('/data')
      .then((res) => res.json())
      .then((rest) => {
        this.state.counter= (this.state.counter%rest.paperData.length)
        this.state.title = rest.paperData[counter].title
        this.state.content = rest.paperData[counter].content
      })
      // .then((data) => this.setState({ username: data.paper[0]['title'] }));
  }

  handleNextTitleButtonClick = () => {
    this.state.counter += 1
    this.componentDidMount()
  }

  render() {
    const { title, content}  = this.state;
    console.log("App - Rendered");
    return (
      <React.Fragment>
        <NavBar />
        <button onClick={this.handleNextTitleButtonClick}>Go to next title</button>
        <h2>{ `${title}` }</h2>
        <p>{ `${content}` }</p>
      </React.Fragment>
    );
  }
}

export default App;
