    this.componentDidMount()
  }

  render() {
    const { title, content}  = this.state;
    console.log("App - Rendered");
    return (
      <React.Fragment>
        <NavBar />
        <button onClick={this.handleNextTitleButtonClick}>Go to next title</button>
        <h2>{ `${title}` }</h2>
        <p>{ `${content}` }</p>
      </React.Fragment>
    );
  }
}

export default App;
