import React, { Component } from "react";
import NavBar from "./components/navbar";
import "./App.css";

class App extends Component {
  state = {
    title: null,
    content: null,
    counter: 1,
  };

  constructor(props) {
    super(props);
    console.log("App - Constructor");
  }

  connecToServer() {  fetch('/');  }

  componentDidMount() {
    fetch('/data')
      .then((res) => res.json())
      .then((rest) => {
        this.setState({
          counter : (this.state.counter%this.rest.paperData.length),
          title : this.rest.paperData[counter].title,
          content : this.rest.paperData[counter].content
        })

      })
      // .then((data) => this.setState({ username: data.paper[0]['title'] }));
  }

  handleNextTitleButtonClick = () => {
    this.setState({
      counter : (this.state.counter%rest.paperData.length)
    })
    this.componentDidMount()
  }

  render() {
    const { title, content}  = this.state;
    console.log("App - Rendered");
    return (
      <React.Fragment>
        <NavBar />
        <button onClick={this.handleNextTitleButtonClick}>Go to next title</button>
        <h2>{ `${title}` }</h2>
        <p>{ `${content}` }</p>
      </React.Fragment>
    );
  }
}

export default App;
