import React, { Component } from "react";
import NavBar from "./components/navbar";
import "./App.css";

class App extends Component {
  state = {
    title: null,
    content: null,
    counter: 0
  };

  constructor(props) {
    super(props);
    console.log("App - Constructor");
  }

  connecToServer() {  fetch('/');  }

  componentDidMount() {
    fetch('/data')
      .then((res) => res.json())
      .then((rest) => console.log(rest.paperData[0]))
      // .then((data) => this.setState({ username: data.paper[0]['title'] }));
  }

  render() {
    const { title } = this.state;
    console.log("App - Rendered");
    return (
      <React.Fragment>
        <NavBar />
        <button>Go to next title</button>
        <h2>{ `${title}` }</h2>
      </React.Fragment>
    );
  }
}

export default App;
